-- Room is not yet made available, and also player is not about to get a free room
-- (hide this option in the latter case because it's very confusing for the player)
return not Storage.GetFlag("town_inn_rented_room") and not Player:HasItem("I_MQ01_InnPass")

-- Same conditions as completing MQ02 over at Edgard's dialogue tree
return Storage.GetFlag("MQ02_AMBUSHED") and not Storage.GetFlag("MQ02_DONE")
    and not Storage.GetFlag("INN_ZOSK_TALK_REPAIRS")
